    struct Map_Prototype
    {

        Map_Prototype(const sf::Vector2i &count_blocks, const sf::Vector2i &block_size, const lib::array_2d<int> map_blocks) noexcept : count_blocks_(count_blocks),
                                                                                                                                        total_size_(count_blocks.x * block_size.x, count_blocks.y * block_size.y),
                                                                                                                                        block_size_(block_size),
                                                                                                                                        blocks_(map_blocks)
        {
        }
        Map_Prototype(const Map_Prototype &other) noexcept : count_blocks_(other.count_blocks_),
                                                             total_size_(other.total_size_),
                                                             block_size_(other.block_size_),
                                                             blocks_(other.blocks_)
        {
        }
        Map_Prototype() noexcept : count_blocks_(),
                                   total_size_(),
                                   block_size_(),
                                   blocks_()
        {
        }

        ////////
        // DANGEROUS!!!!!!!!!!!!!!!!!!!! Rewrite create_from_empty and this!
        ///////
        void operator=(const Map_Prototype &other) noexcept
        {
            count_blocks_ = other.count_blocks_;
            total_size_ = other.total_size_;
            block_size_ = other.block_size_;
            blocks_ = other.blocks_;
        }

        sf::Vector2i count_blocks_;
        sf::Vector2i total_size_;
        sf::Vector2i block_size_; // the one block size
        lib::array_2d<int> blocks_;
    };

    struct Block_Prototype
    {

        Block_Prototype(const sf::Vector2i &tile_coords, const sf::Vector2i &size, const std::string &name, const int type) noexcept : tile_coords_(tile_coords),
                                                                                                                                       size_(size),
                                                                                                                                       name_(name),
                                                                                                                                       type_(type)
        {
        }

        /////////////////////////////////////////////////////
        // Check is block solid for other object on map
        /////////////////////////////////////////////////////
        inline bool is_solid() const noexcept { return (type_ >= (CON::SOLID_BLOCK)); }
        inline bool is_sand() const noexcept { return type_ == 2; }
        inline bool is_accel() const noexcept { return type_ == 3; }
        inline bool is_restart() const noexcept { return type_ == 4; }
        inline bool get_type() const noexcept { return type_; }

        const sf::Vector2i tile_coords_;
        const sf::Vector2i size_;
        const std::string name_; //for debugging and understanding the map
        const int type_;
    };

